mapper.select:
select count(u.id) from unitline u left join station s on u.stcd = s.stcd left join sttype t on s.type = t.code where 1=1
and t.code = #{sttp}
and s.stcd = #{stcd}
and u.name like '%' + #{name} + '%'

select u1.id, u1.name, u1.lname, u1.lid, CONVERT(varchar(100), u1.create_time, 20) as createTime, s.stname, t.name as sttype from unitline u1 left join station s on u1.stcd = s.stcd left join sttype t on s.type = t.code, (select TOP ${page*limit} row_number() OVER (order by u.id desc, u.ID desc) n, u.ID from unitline u left join station s on u.stcd = s.stcd left join sttype t on s.type = t.code where 1=1
and t.code = #{sttp}
and s.stcd = #{stcd}
and u.name like '%' + #{name} + '%'
) u2 where 1=1 and u1.id = u2.id and u2.n > ${(page-1)*limit} order by u2.n asc

mapper.update:
update model
name = #{name}, stcd = #{stcd},
where id = #{id}
