mapper.select:
select count(m.id) from model m left join station s on m.stcd = s.stcd left join sttype t on s.type = t.code where 1=1
and t.code = #{sttp}
and s.stcd = #{stcd}
and m.name like '%' + #{name} + '%'

select m1.id, m1.name, CONVERT(varchar(100), m1.create_time, 20) as createTime, s.stname, t.name as sttype from model m1 left join station s on m1.stcd = s.stcd left join sttype t on s.type = t.code, (select TOP ${page*limit} row_number() OVER (order by id desc, ID desc) n, ID from model) m2 where 1=1
and t.code = #{sttp}
and s.stcd = #{stcd}
and m1.name like '%' + #{name} + '%'
and m1.id = m2.id and m2.n > ${(page-1)*limit} order by m2.n asc

mapper.update:
update model
name = #{name}, stcd = #{stcd},
where id = #{id}
