mapper.select:
select count(r.id) from rain r left join station s on r.stcd = s.stcd left join sttype t on s.type = t.code where 1=1
and t.code = #{sttp}
and s.stcd = #{stcd}
and r.name like '%' + #{name} + '%'

select r1.id, r1.name, CONVERT(varchar(100), r1.create_time, 20) as createTime, s.stname, t.name as sttype from rain r1 left join station s on r1.stcd = s.stcd left join sttype t on s.type = t.code, (select TOP ${page*limit} row_number() OVER (order by r.id desc, r.ID desc) n, r.ID from rain r left join station s on r.stcd = s.stcd left join sttype t on s.type = t.code where 1=1
and t.code = #{sttp}
and s.stcd = #{stcd}
and r.name like '%' + #{name} + '%'
) r2 where 1=1 and r1.id = r2.id and r2.n > ${(page-1)*limit} order by r2.n asc

mapper.update:
update rain
stcd = #{stcd}, name = #{name},
where id = #{id}
